import React, { useState } from 'react';
import './main.css';

const Main = () => {

    var [arr, setArr] = useState([]);
    var [balance, setBalance] = useState(0);
    const expenseContainer = () => {
        document.querySelector("#formContainer").style = "display:block;"
    }
    const moneyContainer = () => {
        document.querySelector("#addMoneyContainer").style = "display:block;"
    }
    const cross = () => {
        document.querySelector("#formContainer").style = "display:none;"
        document.querySelector("#addMoneyContainer").style = "display:none;"
    }
    const addMoney = (e) => {
        e.preventDefault();
        const inp = Number(document.querySelector("#moneyAdd").value);
        const notes = document.querySelector("#notes").value;
        if (inp == "") {
            document.querySelector("#warn").innerHTML = "Enter some money.";
            document.querySelector("#warn").style = "color:red";
        } else {
            var bal = inp + balance;
            setBalance(bal);
            var obj = { inp, notes };
            setArr([...arr, obj]);
            var li = document.createElement("li");
            li.innerHTML = `<span class="inpE">${inp}</span><span class="noteE">${notes}</span>`;
            document.querySelector(".all").appendChild(li);
        }
    }
    const addExpense = (e) => {
        e.preventDefault();
        const inp = Number(document.querySelector("#moneyExpense").value);
        const category = document.querySelector("#category :checked").value;
        const note = document.querySelector("#noteExpense").value;
        if (inp == "" || category === "") {
            document.querySelector("#warning").innerHTML = "Enter both money and category.";
            document.querySelector("#warning").style = "color:red";
        }
        else {
            var li = document.createElement("li");
            li.innerHTML = `<div class='listCard' category=${category}>
            <div class='listDetail'><span>${inp}</span> </div>
            <div class='listNote'>Note : ${note} </div>
        </div>`
            document.querySelector(".all").appendChild(li);
            if (category === "grocery") {
                li = document.createElement("li");
                li.innerHTML = `<div class='listCard' category=${category}>
                <div class='listDetail'><span>${inp}</span> <span><i class="fa-solid fa-pen-to-square" onClick={edit}></i> <i class="fa-solid fa-trash" onClick={del}></i></span>
                </div>
                <div class='listNote'>Note : ${note} </div>
            </div>`;
                document.querySelector(".groc").appendChild(li);
            }

        }
    }
    const edit = (e) => {

    }
    const del = (e) => {

    }

    return (
        <>
            <div id="container">
                <div id="balanceContainer"><h3> Your Balance</h3>
                    <p id="balance"><h6> ₹ </h6><h5>{balance}</h5></p>
                </div>
                <div id="historyContainer">

                    <div className='cards' key="history"><h1 > <img src='https://mpng.subpng.com/20201108/wae/transparent-book-icon-history-icon-5fa8a18cbad7d7.9650804216048869247653.jpg' alt='All-history' />
                        Transaction History</h1></div>
                        <div className='cards' key="grocery"><h1> <img src='https://cdn-icons-png.flaticon.com/512/3724/3724788.png' alt='All-history' />
                        Groceries</h1></div>
                    <div className='cards' key="travelling"><h1> <img src='https://cdn-icons-png.flaticon.com/512/201/201623.png' alt='Travelling' />  Travelling</h1></div>
                    <div className='cards' key="veggies"><h1><img src='https://www.clipartmax.com/png/middle/361-3612050_clipart-royalty-free-library-vegetable-bowl-icons-png-vegetable-bowl-vector-png.png' alt='Veggies' /> Veggies</h1></div>
                    <div className='cards' key="misc"><h1><img src='https://f.hubspotusercontent30.net/hubfs/3277184/ICONS/ICON-MISC-PersonCheckStatus-GRADIENT.png' alt='Miscellaneous' /> Miscellaneous</h1></div>

                </div>
                <div id="add"><h1 id="addExpense" onClick={expenseContainer}> Add Expense</h1><h1 id="addMoney" onClick={moneyContainer}>Add Money </h1></div>

                <div id="formContainer">
                    <i className="fa-solid fa-xmark" onClick={cross} ></i>
                    <form className="form">
                        <p style={{ fontSize: "2rem", fontWeight: "800", marginBottom: "5%" }}>Add Detail</p>
                        <div> <input type="number" placeholder="Enter Money" id='moneyExpense' required /></div>
                        <div>
                            <select id='category'>Select the category
                                <option value="-1" disabled>Select the category</option>
                                <option value="grocery">Grocery</option>
                                <option value="veggies">Veggies</option>
                                <option value="travelling">Travelling</option>
                                <option value="misc">Miscellaneous</option>
                            </select>
                        </div>
                        <div><input type="text" placeholder="Notes" id='noteExpense' /></div>

                        <p id="warning"></p>
                        <button name="submit" className="btn" onClick={addExpense} >Add Detail</button>
                    </form>
                </div>

                <div id='addMoneyContainer'>
                    <i className="fa-solid fa-xmark" onClick={cross} ></i>
                    <form className="form">
                        <p style={{ fontSize: "2rem", fontWeight: "800", marginBottom: "5%" }}>Add Money</p>
                        <div> <input type="number" placeholder="Enter Money" id='moneyAdd' required /></div>

                        <div><input type="text" placeholder="Notes" id='notes' /></div>

                        <p id="warn"></p>
                        <button name="submit" className="btn" onClick={addMoney}>Add</button>
                    </form>
                </div>

                    <div id='detailContainer'>
                    <i className="fa-solid fa-xmark" onClick={cross} ></i>
                    </div>
            </div >

        </>
    )
}

export default Main
